import { HttpClient, HttpErrorResponse } from "@angular/common/http";
import { Injectable } from "@angular/core";
import { catchError, tap } from "rxjs/operators";
import { Subject, throwError } from "rxjs";
import { User } from "./user.model";

export interface AuthResponseData {
    kind: string;
    idToken: string;
    email: string;
    refreshToken: string;
    expiresIn: string;
    localId: string;
    registered?: boolean;
}


@Injectable({ 
    providedIn: 'root' 
})
export class AuthService{

    user = new Subject<User>();

    constructor(private http: HttpClient){

    }

    signup(email: string, password: string ){
        return this.http.post<AuthResponseData>('https://identitytoolkit.googleapis.com/v1/accounts:signUp?key=AIzaSyBArJ_yqO0_p9KbaJ7fWfeLmwxE0VxXNVs',
        {
            email: email,
            password: password,
            returnSecuredToken: true

        }).pipe(catchError(this.handlError), tap(resData => {
            const expirationDate = new Date(new Date().getTime()+ +resData.expiresIn*1000);
            const user = new User(resData.email, resData.localId, resData.idToken, expirationDate)
            this.user.next(user);
        }));
    }

    login(email: string, password: string){
        return this.http.post<AuthResponseData>('https://identitytoolkit.googleapis.com/v1/accounts:signInWithPassword?key=AIzaSyBArJ_yqO0_p9KbaJ7fWfeLmwxE0VxXNVs',{
            email: email,
            password: password,
            returnSecuredToken: true
        }).pipe(catchError(this.handlError));
    }

    private handleAuthentication(email: string, token: string, expiresIn: number){

    }

    private handlError(errorRes: HttpErrorResponse){

        let errorMsg = "An Unknown error has been occured"
        console.log("--------------");
        console.log(errorRes);
        console.log("--------------");
        console.log(errorRes.error.error);

        // errorRes.error && errorRes.error.error
        if(!errorRes.error || !errorRes.error.error){
            return throwError(errorMsg);
        }

        switch( errorRes.error.error.message){
            case 'EMAIL_EXISTS': errorMsg = "An Email Exists Already";
                                break;
            case 'EMAIL_NOT_FOUND': errorMsg = "This Email does not Exist";
            break;
            case 'INVALID_PASSWORD': errorMsg = "Password is Incorrect";
            break;

        }
        return throwError(errorMsg);       
    }
}
