import { Ingredients } from "../shared/ingredients.model";
import { Subject } from "rxjs";
export class ShoppingListService {
  constructor() {
    this.ingredientsCHange = new Subject();
    this.statedEditing = new Subject();
    this.ingredients = [new Ingredients('Apple', 5), new Ingredients('Mango', 9)];
  }
  getIngredients() {
    return this.ingredients.slice();
  }
  getIngredient(index) {
    return this.ingredients[index];
  }
  addIngredient(ingredinet) {
    this.ingredients.push(ingredinet);
    this.ingredientsCHange.next(this.ingredients.slice());
  }
  addIngredients(ingredients) {
    this.ingredients.push(...ingredients);
    this.ingredientsCHange.next(this.ingredients.slice());
  }
  updateIngredients(index, newIngredinet) {
    this.ingredients[index] = newIngredinet;
    this.ingredientsCHange.next(this.ingredients.slice());
  }
  deleteIngredinet(index) {
    this.ingredients.splice(index, 1);
    this.ingredientsCHange.next(this.ingredients.slice());
  }
}
