import { EventEmitter } from "@angular/core";
import { Recipe } from "./recipe.model";

export class RecipeSerive{

    recipeSeleted = new EventEmitter<Recipe>();
    private recipes: Recipe[] = [
        new Recipe('A test recipe', 'this is a simple test', 'https://theurbantandoor.com/wp-content/uploads/2019/09/paneer-butter-masala.jpg'),
        new Recipe('A test recipe2', 'this is a simple test', 'https://theurbantandoor.com/wp-content/uploads/2019/09/paneer-butter-masala.jpg')
      ];

    getRecipes(){
        return this.recipes.slice();
    }
}
