import { EventEmitter } from "@angular/core";
import { Recipe } from "./recipe.model";

export class RecipeService{

    recipeSeleted = new EventEmitter<Recipe>();
    private recipes: Recipe[] = [
        new Recipe('Butter Panner Masala', 'this is a simple test', 'https://theurbantandoor.com/wp-content/uploads/2019/09/paneer-butter-masala.jpg'),
        new Recipe('Shahi Paneer', 'this is a simple test', 'https://theurbantandoor.com/wp-content/uploads/2019/09/paneer-butter-masala.jpg')
      ];

    getRecipes(){
        return this.recipes.slice();
    }
}
